declare enum Breakpoints {
    xs = "xs",
    sm = "sm",
    md = "md",
    lg = "lg",
    xl = "xl"
}
declare const breakpoints: {
    xl: number;
    lg: number;
    md: number;
    sm: number;
    xs: number;
};

declare type ResponsiveProperty<Type> = Partial<Record<Breakpoints, Type>>;

declare type height = ResponsiveProperty<string | number> | string;
declare type width = ResponsiveProperty<string | number> | string;
declare type background = ResponsiveProperty<string> | string;
declare type color = ResponsiveProperty<string> | string;

declare type flexDirection = ResponsiveProperty<"row" | "column"> | "row" | "column";
declare type flex = ResponsiveProperty<number | string> | string | number;
declare type alignItems = ResponsiveProperty<"stretch" | "flex-start" | "flex-end" | "center"> | "stretch" | "flex-start" | "flex-end" | "center";
declare type justifyContent = ResponsiveProperty<"center" | "flex-start" | "flex-end" | "space-between" | "space-around" | "space-evenly"> | "center" | "flex-start" | "flex-end" | "space-between" | "spacearound" | "space-evenly";

interface StyleSheet {
    height?: height;
    width?: width;
    color?: color;
    backgroundColor?: background;
    /** https://developer.mozilla.org/en-US/docs/Web/CSS/flex */
    flex?: flex;
    /** https://developer.mozilla.org/pt-BR/docs/Web/CSS/align-items */
    alignItems?: alignItems;
    /** It is equivalent to alignItems */
    crossAxisAlignment?: alignItems;
    /** https://developer.mozilla.org/en-US/docs/Web/CSS/justify-content */
    justifyContent?: justifyContent;
    /** It is equivalent to justifyContent */
    mainAxisAlignment?: justifyContent;
    flexDirection?: flexDirection;
    hover?: StyleSheet;
    focus?: StyleSheet;
    modeDark?: StyleSheet;
    [key: string]: ResponsiveProperty<string | any> | string | any;
}

declare function parseStyleSheet(styleSheet: unknown, uniqueId?: string): any;

export { Breakpoints, ResponsiveProperty, StyleSheet, breakpoints, parseStyleSheet };
